// Card.jsx

import React from 'react';
import { Link } from 'react-router-dom';
import { useCharStates } from '../Context/Context';
import '../App.css';

const Card = ({ item, isFavoriteSection }) => {
  const { favs, dispatch } = useCharStates();
  const findFav = favs.find((fav) => fav.id === item.id);

  const addFav = () => {
    if (findFav) {
      alert('Ese personaje ya est√° agregado a favoritos');
    } else {
      dispatch({ type: 'ADD_FAV', payload: item });
    }
  };

  // Agrega una clase especial para las cards de la secci√≥n de favoritos
  const cardClassName = isFavoriteSection ? 'FavCard' : 'Card';

  return (
    <div className={`Boxes ${cardClassName}`}>
      <Link to={`/detail/${item.id}`}>
        <img src="/images/doctor.jpg" className='Docimg' alt="" />
        <h4>{item.name}</h4>
        <p>{item.username}</p>
      </Link>
      <button onClick={addFav}>{findFav ? 'üíñ' : '‚ù§'}</button>
    </div>
  );
};

export default Card;
